      get :new
      assigns(:lookup_hardware).should be(mock_lookup_hardware)
    end
  end

  describe "GET edit" do
    it "assigns the requested lookup_hardware as @lookup_hardware" do
      LookupHardware.stub(:find).with("37") { mock_lookup_hardware }
