        LookupHardware.stub(:new).with({'these' => 'params'}) { mock_lookup_hardware(:save => false) }
        post :create, :lookup_hardware => {'these' => 'params'}
        assigns(:lookup_hardware).should be(mock_lookup_hardware)
      end

      it "re-renders the 'new' template" do
        LookupHardware.stub(:new) { mock_lookup_hardware(:save => false) }
        post :create, :lookup_hardware => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested lookup_hardware" do
